package com.pls.utils;

import com.pls.enums.LogType;
import com.pls.driver.DriverManager;
import com.pls.reports.FrameworkLogger;
import org.openqa.selenium.OutputType;
import org.openqa.selenium.TakesScreenshot;

public final class ScreenshotUtils {

    private ScreenshotUtils() {
    }

    public static String getBase64Image() {
        String capture = "data:image/webp;base64,";
        try {
            capture = ((TakesScreenshot) DriverManager.getDriver()).getScreenshotAs(OutputType.BASE64);
        } catch (Exception e) {
            FrameworkLogger.log(LogType.CONSOLE, "Not possible to take an screenshot");
        }
        return capture;
    }

}